mapper.select:
SELECT backerId, backerAccount, roleId, addTime
FROM backer_tab
WHERE backerId = #{backerId}

SELECT COUNT(backerAccount)
FROM backer_tab
WHERE backerAccount = #{backerAccount}

	   SELECT backerId, backerAccount, roleId, addTime
       FROM backer_tab
	   WHERE backerAccount = #{backerAccount}
	   AND password = #{password}

mapper.update:
	   UPDATE backer_tab
	   SET password = #{newPassword}
	   WHERE backerId = #{backerId}
       AND password = #{oldPassword}

UPDATE backer_tab
SET password = #{newPassword}
WHERE backerId = #{backerId}

	   UPDATE backer_tab
       SET roleId = #{roleId}
       WHERE backerId = #{backerId}

mapper.select:
SELECT COUNT(*)
FROM backer_tab
WHERE roleId = #{roleId}
